const siFsKesayangan = require('fs');
const jalanSetapak = require('path');
const httpsPahlawan = require('https');

const tukangSedotSpotify = {
  _fetchPakeTenagaDalam(alamatURL, opsiSantai = {}) {
    return new Promise((berhasil, gagal) => {
      const objekURL = new URL(alamatURL);

      const permintaan = httpsPahlawan.request(objekURL, opsiSantai, (jawabanServer) => {
        let emberData = [];

        jawabanServer.on('data', (remah) => emberData.push(remah));

        jawabanServer.on('end', () => {
          const tipeKonten = jawabanServer.headers['content-type'] || '';

          if (tipeKonten.includes('application/json')) {
            try {
              const hasilNgobrolJSON = JSON.parse(Buffer.concat(emberData));
              berhasil({
                ok: jawabanServer.statusCode === 200,
                status: jawabanServer.statusCode,
                headers: jawabanServer.headers,
                json: async () => hasilNgobrolJSON
              });
            } catch (otakError) {
              gagal(otakError);
            }
          } else {
            berhasil({
              ok: jawabanServer.statusCode === 200,
              status: jawabanServer.statusCode,
              headers: jawabanServer.headers,
              buffer: async () => Buffer.concat(emberData)
            });
          }
        });
      });

      permintaan.on('error', gagal);

      if (opsiSantai.body) permintaan.write(opsiSantai.body);
      permintaan.end();
    });
  },

  _cukurNamaFileBiarSopan(namaAsli) {
    return namaAsli.replace(/[\\/:*?"<>|]/g, '').trim();
  },

  async sedotDanSimpan(linkSpotifyKesayangan) {
    const alamatDetailLagu =
      "https://spotdown.org/api/song-details?url=" +
      encodeURIComponent(linkSpotifyKesayangan);

    try {
      const responDetail = await this._fetchPakeTenagaDalam(alamatDetailLagu, {
        method: "GET",
        headers: {
          "accept": "application/json, text/plain, */*",
          "accept-language": "id-ID,id;q=0.9,en-US;q=0.8,en;q=0.7",
          "cache-control": "no-cache",
          "pragma": "no-cache",
          "sec-ch-ua": "\"Chromium\";v=\"139\", \"Not;A Brand\";v=\"99\"",
          "sec-ch-ua-mobile": "?1",
          "sec-ch-ua-platform": "\"Android\"",
          "sec-fetch-dest": "empty",
          "sec-fetch-mode": "cors",
          "sec-fetch-site": "same-origin",
          "Referer": "https://spotdown.org/id",
          "Referrer-Policy": "no-referrer-when-downgrade"
        }
      });

      if (!responDetail.ok) {
        throw new Error(`Detail lagu ngambek (HTTP ${responDetail.status})`);
      }

      const dataDetailLagu = await responDetail.json();

      const kukiRahasiaServer = responDetail.headers['set-cookie']
        ? responDetail.headers['set-cookie'].join('; ')
        : null;

      let namaFileAkhir = "lagu-tanpa-nama.mp3";

      if (dataDetailLagu.data?.title && dataDetailLagu.data?.artist) {
        const judulRapi = this._cukurNamaFileBiarSopan(dataDetailLagu.data.title);
        const artisRapi = this._cukurNamaFileBiarSopan(dataDetailLagu.data.artist);
        namaFileAkhir = `${judulRapi} - ${artisRapi}.mp3`;
      }

      const lokasiPenyimpanan = jalanSetapak.resolve(__dirname, namaFileAkhir);

      const responDownload = await this._fetchPakeTenagaDalam(
        "https://spotdown.org/api/download",
        {
          method: "POST",
          headers: {
            "accept": "*/*",
            "accept-language": "id-ID,id;q=0.9,en-US;q=0.8,en;q=0.7",
            "cache-control": "no-cache",
            "content-type": "application/json",
            "pragma": "no-cache",
            ...(kukiRahasiaServer ? { Cookie: kukiRahasiaServer } : {}),
            "Referer": "https://spotdown.org/id",
            "Referrer-Policy": "no-referrer-when-downgrade"
          },
          body: JSON.stringify({ url: linkSpotifyKesayangan })
        }
      );

      if (!responDownload.ok) {
        throw new Error(`Download batal (HTTP ${responDownload.status})`);
      }

      const tipeFile = responDownload.headers['content-type'];

      if (tipeFile?.includes('application/json')) {
        const pesanSedih = await responDownload.json();
        throw new Error(pesanSedih.message || "Server menolak dengan halus");
      }

      const bufferLaguMantap = await responDownload.buffer();
      siFsKesayangan.writeFileSync(lokasiPenyimpanan, bufferLaguMantap);

      return {
        detail: dataDetailLagu,
        namaFile: namaFileAkhir,
        path: lokasiPenyimpanan,
        ukuran: bufferLaguMantap.length,
        tipe: tipeFile || "audio/mpeg",
        status: "berhasil_diculik_ke_disk"
      };
    } catch (kesalahanHidup) {
      return {
        error: true,
        message: kesalahanHidup.message
      };
    }
  }
};

