const axios = require("axios");
const cheerio = require("cheerio");

class NasaNgabersService {
  static async cariSemesta(kataSakti) {
    try {
      const { data: htmlDariLuarAngkasa } = await axios.get(
        `https://www.nasa.gov/?search=${encodeURIComponent(kataSakti)}`
      );

      const $ = cheerio.load(htmlDariLuarAngkasa);
      const koperHasil = [];

      $("a.hds-search-result").each((_, asteroid) => {
        const judulGalaksi = $(asteroid)
          .find(".hds-search-result-heading")
          .text()
          .trim();

        const linkTeleport = $(asteroid).attr("href");

        const cuplikanKosmik = $(asteroid)
          .find(".hds-search-result-excerpt")
          .text()
          .trim();

        const fotoNebula = $(asteroid)
          .find(".hds-search-result-thumbnail img")
          .attr("src");

        koperHasil.push({
          title: judulGalaksi,
          url: linkTeleport,
          excerpt: cuplikanKosmik,
          image: fotoNebula ? `https://www.nasa.gov${fotoNebula}` : null
        });
      });

      return koperHasil;
    } catch (ledakanSupernova) {
      console.error("NASA gagal take off:", ledakanSupernova.message);
      return [];
    }
  }

  static async bedahArtikel(linkMisterius) {
    try {
      const { data: htmlRahasia } = await axios.get(linkMisterius);
      const $ = cheerio.load(htmlRahasia);

      const judulUtamaBoss = $("h1.display-48").text().trim();

      const penulisLegend = $("h3.hds-meta-heading")
        .text()
        .trim();

      const tanggalBerangkat = $(".article-meta-item span.heading-12.text-uppercase")
        .first()
        .text()
        .trim();

      const gambarPamer = $("figure img").attr("src");

      const isiPanjangLebar = $(".entry-content")
        .text()
        .trim();

      return {
        title: judulUtamaBoss,
        author: penulisLegend,
        date: tanggalBerangkat,
        image: gambarPamer || null,
        content: isiPanjangLebar
      };
    } catch (alienMenyerang) {
      console.error("Artikel diculik alien:", alienMenyerang.message);
      return null;
    }
  }
}

