import { fetch } from "undici";

class CloudflareDNS {
  constructor(apiKey, zoneId) {
    this.apiKey = apiKey;
    this.zoneId = zoneId;
    this.baseURL = `https://api.cloudflare.com/client/v4/zones/${zoneId}/dns_records`;
  }

  get headers() {
    return {
      "Authorization": `Bearer ${this.apiKey}`,
      "Content-Type": "application/json"
    };
  }

  async listDNS() {
    const res = await fetch(this.baseURL, { headers: this.headers });
    return res.json();
  }

  async addRecord({ type, name, content, ttl = 3600, proxied = false }) {
    const body = { type, name, content, ttl, proxied };
    const res = await fetch(this.baseURL, {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return res.json();
  }

  async editRecord(recordId, { type, name, content, ttl = 3600, proxied = false }) {
    const body = { type, name, content, ttl, proxied };
    const res = await fetch(`${this.baseURL}/${recordId}`, {
      method: "PUT",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return res.json();
  }

  async deleteRecord(recordId) {
    const res = await fetch(`${this.baseURL}/${recordId}`, {
      method: "DELETE",
      headers: this.headers
    });
    return res.json();
  }

  async getRecordByName(name) {
    const res = await fetch(`${this.baseURL}?name=${encodeURIComponent(name)}`, {
      headers: this.headers
    });
    return res.json();
  }
}

export default CloudflareDNS;

