Instagramdl("https://www.instagram.com/reel/DOU4ysmD_Q9/")

const cheerio = require("cheerio");

async function downloadPinterest(inputUrl) {
  const url = "https://www.expertstool.com/download-pinterest-video-online/";

  const res = await fetch(url, {
    method: "POST",
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
      "Accept": "*/*"
    },
    body: "url=" + encodeURIComponent(inputUrl)
  });

  const html = await res.text();
  return parsePinterest(html);
}

function parsePinterest(html) {
  const $ = cheerio.load(html);

  const videoUrl = $("video").attr("src") || null;

  const videoDownloads = [];
  $("a[href$='.mp4']").each((i, el) => {
    videoDownloads.push({
      label: $(el).text().trim(),
      url: $(el).attr("href")
    });
  });

  const imageUrl = $("img").first().attr("src") || null;

  const imageDownloads = [];
  $("a[href$='.jpg'], a[href$='.jpeg'], a[href$='.png']").each((i, el) => {
    imageDownloads.push({
      label: $(el).text().trim(),
      url: $(el).attr("href")
    });
  });

  return {
    videoUrl,
    videoDownloads,
    imageUrl,
    imageDownloads
  };
}

